UPDATE storage.objects SET metadata = jsonb_set(coalesce(metadata, '{}'), '{cacheControl}', '"max-age=3600"') WHERE bucket_id = 'blog-images'; ALTER POLICY "Enable public read for blog images" ON storage.objects DROP RESTRICTION; CREATE POLICY "Enable public read for blog images" ON storage.objects FOR SELECT USING (bucket_id = 'blog-images'); ALTER POLICY "Enable upload for authenticated users" ON storage.objects DROP RESTRICTION; CREATE POLICY "Enable upload for authenticated users" ON storage.objects FOR INSERT WITH CHECK (bucket_id = 'blog-images' AND auth.uid() IS NOT NULL);
